  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'advice-generator';
  advice: any;

  constructor(private adviceService:AdviceService){
  }
  
  ngOnInit{

  this.adviceService.getAdvice().subscribe(

(data:any) => {

this.advice = data;

},

(error:any) => {
  console.log(error);

  
}
